import React, { useState } from "react";
import "./HiddenSearchBar.scss";
/* For font awesome icons */
import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSearch,
  faQuestionCircle,
  faCirclePlay,
  faUser,
} from "@fortawesome/free-solid-svg-icons";

const Sidebar = ({ setGridTemplateRows }) => {
  // useState Code
  const [uiProps, setUiProps] = useState({
    transition: "all 2s ease",
    opacity: 0,
    showJustIcon: true,
  });

  const handleClick = () => {
    setGridTemplateRows(
      "10rem 10rem calc(100vh - 20rem) 80vh min-content repeat(2, min-content)"
    );
  };

  let inputStyle = {
    opacity: uiProps.opacity,
    visibility: uiProps.showJustIcon ? "hidden" : "visible",
    height: uiProps.showJustIcon ? 0 : "auto",
    width: uiProps.showJustIcon ? 0 : "100%",
  };

  const showJustIcon = () => {
    setUiProps({
      opacity: 1,
      showJustIcon: false,
    });
  };

  //JSX code
  return (
    <div className="hidden-searchbar__container">
      <input
        style={inputStyle}
        className="hidden-searchbar__input"
        type="text"
        placeholder="Search..."
      />
      {/* if uiProps doesn't exist the search bar disappears */}
      {uiProps.showJustIcon ? (
        <FontAwesomeIcon
          className="hidden-searchbar__icon hidden-searchbar__icon--search"
          icon={faSearch}
          onClick={(showJustIcon, handleClick)}
        ></FontAwesomeIcon>
      ) : (
        <FontAwesomeIcon
          className="hidden-searchbar__icon hidden-searchbar__icon--search"
          icon={faSearch}
        ></FontAwesomeIcon>
      )}
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--question"
        icon={faQuestionCircle}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--player"
        icon={faCirclePlay}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--user"
        icon={faUser}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
    </div>
  );
};

export default Sidebar;
