import React, { useState } from "react";
import "./HiddenSearchBar.scss";
/* For font awesome icons */
import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCart,
  faQuestionCircle,
  faCirclePlay,
  faUser,
} from "@fortawesome/free-solid-svg-icons";

const Sidebar = () => {
  //JSX code
  return (
    <div className="hidden-searchbar__container">
      <input
        style={inputStyle}
        className="hidden-searchbar__input"
        type="text"
        placeholder="Search..."
      />
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--cart"
        icon={faCart}
      ></FontAwesomeIcon>
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--question"
        icon={faQuestionCircle}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--player"
        icon={faCirclePlay}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--user"
        icon={faUser}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
    </div>
  );
};

export default Sidebar;
