import React, { useState } from "react";
import "./HiddenSearchBar.scss";
/* For font awesome icons */
import { library } from "@fortawesome/fontawesome-svg-core";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSearch,
  faQuestionCircle,
  faCirclePlay,
  faUser,
} from "@fortawesome/free-solid-svg-icons";

const Sidebar = ({ handleHeightChange }) => {
  // useState Code
  // for search icon
  const [uiProps, setUiProps] = useState({
    transition: "all 2s ease",
    opacity: 0,
    showJustIcon: true,
  });

  const increaseSearchBarSize = () => {
    setContainerHeight("10rem");
  };

  let inputStyle = {
    opacity: uiProps.opacity,
  };

  const showJustIcon = () => {
    setUiProps({
      opacity: 1,
      showJustIcon: false,
    });
  };

  //JSX code
  return (
    <div
      style={{ height: searchContainerHeight }}
      className="hidden-searchbar__container"
    >
      <input
        style={inputStyle}
        className="hidden-searchbar__input"
        type="text"
        placeholder="Search..."
      />
      {/* if uiProps doesn't exist the search bar disappears */}
      {uiProps.showJustIcon ? (
        <FontAwesomeIcon
          className="hidden-searchbar__icon hidden-searchbar__icon--search"
          icon={faSearch}
          onClick={(showJustIcon, searchBarStyles)}
        ></FontAwesomeIcon>
      ) : (
        <FontAwesomeIcon
          className="hidden-searchbar__icon hidden-searchbar__icon--search"
          icon={faSearch}
        ></FontAwesomeIcon>
      )}
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--question"
        icon={faQuestionCircle}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--player"
        icon={faCirclePlay}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
      <FontAwesomeIcon
        className="hidden-searchbar__icon hidden-searchbar__icon--user"
        icon={faUser}
        onClick={showJustIcon}
      ></FontAwesomeIcon>
    </div>
  );
};

export default Sidebar;
